import React, {Component} from 'react';
import axios from 'axios';

import MainVideo from './components/MainVideo';

import './style/App.css';
import { response } from 'express';

const api_key = '8531d6ee-2e2b-411a-8aad-f9f62c07352f';

class App extends Component {
  state = {
    mainVideo: {},
    sideVideo: [],
  };

  componentDidMount() {
    this.getMainVideoData('1af0jruup5gu');
    this.getVideoListData();
  }

  getMainVideoData(id) {
      // axios.get(`https://project-2-api.herokuapp.com/videos/${id}/?api_key=${api_key}`)
      axios.get(`http://localhost:8080/videos/${id}`)
      .then((response) => {
        // console.log(response);
        const data = response.data[0];
        this.setState({
            mainVideo: data,
        });
      })
      .catch((error) => console.log(error));
  }

  getVideoListData() {
      // axios.get(`https://project-2-api.herokuapp.com/videos/?api_key=${api_key}`)
      axios.get(`http://localhost:8080/videos/`)
      .then((response) => {
        const {data} = response;
        this.setState({
            sideVideo: data,
        });
      })
  }

  postComment() {
    // axios.post(`http://localhost:8080/videos/:id/comments`, {
    //   name: name
    // })

  }

  componentDidUpdate(prevProps, prevState) {
    const {params} = this.props.match;

    if(params.id && prevState.mainVideo.id !== params.id) {
      this.getMainVideoData(params.id);
    }
  }

  render() {
    return (
      <div className="app">
        {/* <Header /> */}
        <MainVideo 
          mainVideo={this.state.mainVideo} 
          api_key={api_key} 
          sideVideo={this.state.sideVideo}
          activeVideo={this.state.mainVideo.id}
        />
      </div>
    );
  }
}

export default App;
