import React, {Component} from 'react';

class Comment extends Component {
    render() {
        let {name, comment, likes, time, likesIcon, avatar} = this.props;
        // let {name, comment, time, avatar} = this.props;

        return (
            <li>
                <div><img className="comment__avatar" src={avatar} /></div>
                <h4>{name}</h4>
                <time>{time}</time>
                <p>{comment}</p>
                <p>
                    <img src={likesIcon} />
                    {likes}
                </p>
            </li>
        )
    }
}

export default Comment;
