import React, {Component} from 'react';
import axios from 'axios';

import MainVideo from './components/MainVideo';
import NextVideo from './components/NextVideo';

import './style/App.css';

const api_key = '8531d6ee-2e2b-411a-8aad-f9f62c07352f';

class App extends Component {
  state = {
    mainVideo: {},
    sideVideo: [],
  };

  componentDidMount() {
    this.getMainVideoData('1af0jruup5gu');
    this.getVideoListData();
  }

  getMainVideoData(id) {
      axios.get(`https://project-2-api.herokuapp.com/videos/${id}/?api_key=${api_key}`)
      .then((response) => {
        const {data} = response;
        this.setState({
            mainVideo: data,
        });
      })
      .catch((error) => console.log(error));
  }

  getVideoListData() {
      axios.get(`https://project-2-api.herokuapp.com/videos/?api_key=${api_key}`)
      .then((response) => {
        const {data} = response;
        this.setState({
            sideVideo: data,
        });
      })
  }

  // componentDidUpdate(prevProps, prevState) {
  //   const {params} = this.props.match;

  //   if(paramas.id && prevState.mainVideo.id !== params.id) {
  //     this.getMainVideoData(param.id);
  //   }
  // }

  render() {
    return (
      <div className="app">
        {/* <Header /> */}
        <MainVideo mainVideo={this.state.mainVideo} api_key={api_key}/>
        <NextVideo sideVideo={this.state.sideVideo} />
      </div>
    );
  }
}

export default App;
