import React, {Component} from "react";
// import Comment from "./Comment";

class MainVideo extends Component {
    state = {
        mainVideo: [ 
            {
                "id": "1af0jruup5gu",
                "title": "BMX Rampage: 2018 Highlights",
                "channel": "Red Cow",
                // "image": "https://i.imgur.com/l2Xfgpl.jpg",
                "image": "./images/video-list-0.jpg",
                "description": "On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time -- eight years after his first Red Cow Rampage title",
                "views": "1,001,023",
                "likes": "110,985",
                "duration": "4:01",
                // "video": "https://project-2-api.herokuapp.com/stream",
                "video": "./video/BrainStation Sample Video.mp4",
                "timestamp": new Date(1545162149000),
                "comments": [
                    {
                        "name": "Micheal Lyons",
                        "comment": "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of concert I have EVER witnessed.",
                        "id": "1ab6d9f6-da38-456e-9b09-ab0acd9ce818",
                        "likes": 0,
                        "timestamp": new Date(1545162149000).toLocaleDateString()
                    },
                    {
                        "name": "Gary Wong",
                        "comment": "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!",
                        "id": "cc6f173d-9e9d-4501-918d-bc11f15a8e14",
                        "likes": 0,
                        "timestamp": new Date(1544595784046).toLocaleDateString()
                    },
                    {
                        "name": "Theodore Duncan",
                        "comment": "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!",
                        "id": "993f950f-df99-48e7-bd1e-d95003cc98f1",
                        "likes": 0,
                        "timestamp": new Date(1542262984046).toLocaleDateString()
                    },
                ],
            },
        ],
    };

    render() {
        let {mainVideo} = this.state;

        return (
            <section className="main-video" >
                <video
                    controls
                    className="main-video__video"
                    poster= {mainVideo[0].image}
                    src= {mainVideo[0].video}
                ></video>
                <div className="main-video__detail">
                    <h1 className="main-video__title">{mainVideo[0].title}</h1>
                    <h2 className="main-video__channel">By {mainVideo[0].channel}</h2>
                    <time>{mainVideo[0].timestamp.toLocaleDateString()}</time>
                    <p>
                        <img src={this.props.viewsIcon} />
                        {mainVideo[0].views}
                    </p>
                    <p>
                        <img src={this.props.likesIcon} />
                        {mainVideo[0].likes}
                    </p>
                    <p>
                        {mainVideo[0].description}
                    </p>
                </div>
                <div className="comment">
                    <h3>{mainVideo[0].comments.length} Comments</h3>
                    <div className="comment__part">
                        <img className="comment__avatar" src={this.props.avatar} />
                        <form className="comment__form">
                            <label for="comment" className="conversation__label">JOIN THE CONVERSATION</label>
                            <textarea className="conversation__comment" cols="30" rows="5" placeholder="That was easily the most spectacular BMX moment ever."></textarea>
                            <input type="submit" value="COMMENT" />
                        </form>
                        <ul>
                            {mainVideo[0].comments.map((comment) => (
                                // <Comment 
                                //     key= {comment.id}
                                //     name= {comment.name}
                                //     comment= {comment.comment}
                                //     time= {comment.timestamp.toLocaleDateString()}
                                //     likes= {comment.likes}
                                //     likesLcon= {this.props.likesIcon}
                                // ></Comment>

                                <li>
                                    <img src={this.props.commentsAvatar} />
                                    <h4>{comment.name}</h4>
                                    <time>{comment.time}</time>
                                    <p>{comment.comment}</p>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </section>
        );
    }
}

export default MainVideo;
