import likesIcon from './assets/Icons/PNG/Icon-likes.png';

function Comment (props) {
    return (
        <li>
            <div><img className="comment__avatar" src={props.avatar} /></div>
            <h4>{props.name}</h4>
            <time>{props.time}</time>
            <p>{props.comment}</p>
            <p>
                <img src={likesIcon} />
                {props.likes}
            </p>
        </li>
    )
}

export default Comment;
