    return (
        <li className="comment-list__item">
            <img alt="Sloth Avatar" className="comment-list__avatar" src="./images/sloth-5043323_1920.png" />
            <div className="comment-list__box">
                <h3 className="comment-list__name">{props.name}</h3>
                <time>{props.time}</time>
                <p className="comment-list__comment">{props.comment}</p>
                {/* <p>
                    <img alt="Like Icon" src={props.likesIcon} />
                    {props.likes}
                </p> */}
            </div>
        </li>
    )
}

export default Comment;
