function Comment(props) {
    return (
        <li>
            <img alt="Sloth Avatar" className="comment__avatar" src="./images/sloth-5043323_1920.png" />
            <h4>{props.name}</h4>
            <time>{props.time}</time>
            <p>{props.comment}</p>
            <p>
                <img alt="Like Icon" src={props.likesIcon} />
                {props.likes}
            </p>
        </li>
    )
}

export default Comment;
