import VideoHero from "./VideoHero";
import VideoDetail from "./VideoDetail";
import Comment from "./Comment";
import NextVideo from './NextVideo';

import {formatTime} from './utilities/formatting';
import viewsIcon from '../assets/Icons/PNG/Icon-views.png';
import likesIcon from '../assets/Icons/PNG/Icon-likes.png';

function MainVideo ({mainVideo,sideVideo,activeVideo}) {
    const {comments} = mainVideo;
    return !mainVideo ? <h2>Loading...</h2> :
    (
        <main>
            <VideoHero mainVideo={mainVideo} />
            <div className="content">
                <div className="content__left">
                    <VideoDetail mainVideo={mainVideo} />
                    <Comment comments={comments} />
                </div>
                <NextVideo sideVideo={sideVideo} activeVideo={activeVideo}/>
            </div>
        </main>
    )
}

export default MainVideo;
