import React from 'react';

import Comment from "./Comment";
import NextVideo from './NextVideo';

import {formatTime} from './utilities/formatting';
import viewsIcon from '../assets/Icons/PNG/Icon-views.png';
import likesIcon from '../assets/Icons/PNG/Icon-likes.png';

function MainVideo ({mainVideo,sideVideo,api_key,activeVideo}) {
    const {comments} = mainVideo;
    const videoRef = React.createRef();

    function refreshVideo() {
        videoRef.current.load();
    }

    return !mainVideo ? <h2>Loading...</h2> :
    (
        <main>
            <section className="main-video" >
                <div ref={videoRef} className="main-video__box">
                    <video
                        controls refreshVideo
                        className="main-video__video"
                        poster= {mainVideo.image}
                        src= {`${mainVideo.video}/?api_key=${api_key}`}
                    ></video>
                </div>
                </section>
            <div className="content">
            <div className="content__left">
            <section className="main-video__detail">
                <h1 className="main-video__title">{mainVideo.title}</h1>
                <div className="main-video__container">
                    <h2 className="main-video__channel">By {mainVideo.channel}</h2>
                    <time>{formatTime(mainVideo.timestamp)}</time>
                    <div className="main-video__rating">
                        <p className="main-video__views">
                            <img alt="View Icon" src={viewsIcon} />
                            {mainVideo.views}
                        </p>
                        <p className="main-video__likes">
                            <img alt="Like Icon" src={likesIcon} />
                            {mainVideo.likes}
                        </p>
                    </div>
                </div>
                <p className="main-video__description">
                    {mainVideo.description}
                </p>            
            </section>
            <section className="comment">
                <h2>{comments && comments.length} Comments</h2>
                <div className="comment__box">
                    <img alt="Male Avatar" className="comment__avatar" src="./images/Mohan-muruge.jpg" />
                    <form className="comment__form">
                        <div className="comment__inside">
                            <label htmlFor="comment" className="comment__label">JOIN THE CONVERSATION</label>
                            <textarea className="comment__textarea" cols="27" rows="4" placeholder="Add a comment"></textarea>
                        </div>
                        <input className="comment__submit" type="submit" value="COMMENT" />
                    </form>
                </div>
                <ul className="comment-list">
                    {comments && comments.map((comment) => (
                        <Comment 
                            key= {comment.id}
                            name= {comment.name}
                            comment= {comment.comment}
                            time= {formatTime(comment.timestamp)}
                            likes= {comment.likes}
                            likesIcon= {likesIcon}
                        ></Comment>
                    ))}
                </ul>
            </section>
            </div>
            <NextVideo sideVideo={sideVideo} activeVideo={activeVideo} api_key={api_key} />
            </div>
        </main>
    )
}

export default MainVideo;
