import Comment from "./Comment";
import viewsIcon from '../assets/Icons/PNG/Icon-views.png';
import likesIcon from '../assets/Icons/PNG/Icon-likes.png';

function MainVideo (props) {
    return (
        <section className="main-video" >
            <video
                controls
                className="main-video__video"
                poster= {props.mainVideo.image}
                src= {props.mainVideo.video}
            ></video>
            <div className="main-video__detail">
                <h1 className="main-video__title">{props.mainVideo.title}</h1>
                <h2 className="main-video__channel">By {props.mainVideo.channel}</h2>
                <time>{props.mainVideo.timestamp.toLocaleDateString()}</time>
                <div className="main-video__view">
                    <img alt="View Icon" src={viewsIcon} />
                    <p></p>{props.mainVideo.views}
                </div>
                <p>
                    <img alt="Like Icon" src={likesIcon} />
                    {props.mainVideo.likes}
                </p>
                <p>
                    {props.mainVideo.description}
                </p>
            </div>
            <div className="comment">
                <h3>{props.mainVideo.comments.length} Comments</h3>
                <div className="comment__part">
                    <img alt="Male Avatar" className="comment__avatar" src="./images/Mohan-muruge.jpg" />
                    <form className="comment__form">
                        <label for="comment" className="conversation__label">JOIN THE CONVERSATION</label>
                        <textarea className="conversation__comment" cols="30" rows="5" placeholder="That was easily the most spectacular BMX moment ever."></textarea>
                        <input type="submit" value="COMMENT" />
                    </form>
                    <ul>
                        {props.mainVideo.comments.map((comment) => (
                            <Comment 
                                key= {comment.id}
                                name= {comment.name}
                                // avatar = {props.commentsAvatar}
                                comment= {comment.comment}
                                time= {comment.timestamp.toLocaleDateString()}
                                likes= {comment.likes}
                                likesIcon= {likesIcon}
                            ></Comment>
                        ))}
                    </ul>
                </div>
            </div>
        </section>
    )
}

export default MainVideo;
